import React, { useEffect, useRef, useState } from "react";
import MainHeader from "./MainHeader";
import Navigation from "./Navigation";
import hashiraTheme from "./hashiraTheme.mp3";
import classes from "./HomePage.module.css";

function HomePage() {
  const audioRef = useRef(null);
  const videoRef = useRef(null);
  const [isAudioMuted, setIsAudioMuted] = useState(false);

  useEffect(() => {
    const audio = audioRef.current;
    const video = videoRef.current;

    const playAudio = () => {
      if (audio && !isAudioMuted) {
        audio.volume = 0.5; // Set the volume to 50%
        audio.play().catch((error) => {
          // Autoplay failed, handle the error here
          console.log("Autoplay failed:", error);
        });
      }
    };

    const handleMouseEnter = (event) => {
      if (event.target === video) {
        // Mouse entered the video
        audio.pause();
        video.play();
      } else {
        // Mouse entered the rest of the page
        video.pause();
        playAudio();
      }
    };

    const handleMouseLeave = () => {
      // Mouse left the video or the page
      video.pause();
      audio.pause();
    };

    video.addEventListener("mouseenter", handleMouseEnter);
    video.addEventListener("mouseleave", handleMouseLeave);
    document.addEventListener("mouseenter", handleMouseEnter);
    document.addEventListener("mouseleave", handleMouseLeave);

    return () => {
      video.removeEventListener("mouseenter", handleMouseEnter);
      video.removeEventListener("mouseleave", handleMouseLeave);
      document.removeEventListener("mouseenter", handleMouseEnter);
      document.removeEventListener("mouseleave", handleMouseLeave);
      if (audio) {
        audio.pause();
        audio.currentTime = 0;
      }
    };
  }, [isAudioMuted]);

  const toggleAudio = () => {
    setIsAudioMuted((prevState) => !prevState);
  };

  return (
    <React.Fragment>
      <MainHeader headerTitle="Hashira Fanpage" bannerLoc="/images/banner.jpeg" />
      <div className={classes.container}>
        <video ref={videoRef} src="videos/TheHashiras.mp4" loop muted>
          {/* Add additional video sources if needed */}
        </video>
        <button className={classes.toggleButton} onClick={toggleAudio}>
          {isAudioMuted ? "Unmute" : "Mute"}
        </button>
      </div>
      <Navigation />
      <audio ref={audioRef} src={hashiraTheme} loop />
    </React.Fragment>
  );
}

export default HomePage;
