import React, { useEffect, useRef } from "react";
import MainHeader from "./MainHeader";
import Navigation from "./Navigation";
import hashiraTheme from "./hashiraTheme.mp3"
import classes from "./Navigation.module.css"


function HomePage() {
    const audioRef = useRef(null);

  useEffect(() => {
    const audio = audioRef.current;

    const handleMouseEnter = () => {
      if (audio && audio.paused) {
        audio.volume = 0.5; // Set the volume to 50%
        audio.play().catch((error) => {
          // Autoplay failed, handle the error here
          console.log("Autoplay failed:", error);
        });
      }
    };

    document.addEventListener("mouseenter", handleMouseEnter);

    return () => {
      document.removeEventListener("mouseenter", handleMouseEnter);

      if (audio) {
        audio.pause();
        audio.currentTime = 0;
      }
    };
  }, []);

  return (
    <React.Fragment >
      <MainHeader headerTitle="Hashira Fanpage" bannerLoc="/images/banner.jpeg" />
      <div className={classes.container} on >
      <video>
        
      </video>
    </div>
      <Navigation />
      <audio ref={audioRef} src={hashiraTheme} loop />
    </React.Fragment>
  );
}

export default HomePage;
