import React, { useEffect, useRef, useState } from "react";
import MainHeader from "./MainHeader";
import Navigation from "./Navigation";
import hashiraTheme from "./hashiraTheme.mp3";
import classes from "./HomePage.module.css";

function HomePage() {
  const audioRef = useRef(null);
  const videoRef = useRef(null);
  const [isHovered, setIsHovered] = useState(false);
  const [isAudioOn, setIsAudioOn] = useState(true);
  const [isVideoMuted, setIsVideoMuted] = useState(true);

  useEffect(() => {
    const audio = audioRef.current;

    const playAudio = () => {
      if (audio && isAudioOn) {
        audio.volume = 0.5; // Set the volume to 50%
        audio.play().catch((error) => {
          // Autoplay failed, handle the error here
          console.log("Autoplay failed:", error);
        });
      }
    };

    playAudio(); // Call the function immediately

    return () => {
      if (audio) {
        audio.pause();
        audio.currentTime = 0;
      }
    };
  }, [isAudioOn]);

  const handleAudioToggle = () => {
    setIsAudioOn((prevState) => !prevState);
  };

  const handleVideoToggle = () => {
    setIsVideoMuted((prevState) => !prevState);
    if (videoRef.current) {
      videoRef.current.muted = !isVideoMuted;
    }
  };

  const handleMouseEnter = () => {
    setIsHovered(true);
    if (videoRef.current && !isVideoMuted) {
      videoRef.current.volume = 1; // Set the volume to 100%

      // Check if the video is paused (not playing) and initiate playback
      if (videoRef.current.paused) {
        videoRef.current.play().catch((error) => {
          // Play failed, handle the error here
          console.log("Play failed:", error);
        });
      }
    }
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    if (videoRef.current) {
      videoRef.current.volume = 0; // Set the volume to 0%
      videoRef.current.pause();
      handleAudioToggle();
    }
  };

  return (
    <React.Fragment>
      <MainHeader
        headerTitle="鬼滅の刃"
        bannerLoc="/images/kimetsu-no-yaiba-logo.png"
      />
      <video
        ref={videoRef}
        className={classes.mainVideo}
        src="videos/TheHashiras.mp4"
        autoPlay
        loop
        muted={isVideoMuted}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
      ></video>
      <button
        className={classes.audioToggleBtn}
        onClick={handleAudioToggle}
      >
        {isAudioOn ? "Turn Audio Off" : "Turn Audio On"}
      </button>
      <button
        className={classes.videoToggleBtn}
        onClick={handleVideoToggle}
      >
        {isVideoMuted ? "Unmute Video" : "Mute Video"}
      </button>
      <Navigation />
      <audio ref={audioRef} src={hashiraTheme} loop />
    </React.Fragment>
  );
}

export default HomePage;
