import React, { useEffect, useRef } from "react";
import MainHeader from "./MainHeader";
import Navigation from "./Navigation";
import hashiraTheme from "./hashiraTheme.mp3";
import classes from "./HomePage.module.css";

function HomePage() {
  const audioRef = useRef(null);
  const videoRef = useRef(null);

  useEffect(() => {
    const audio = audioRef.current;
    const video = videoRef.current;

    const playAudio = () => {
      if (audio) {
        audio.volume = 0.5; // Set the volume to 50%
        audio.play().catch((error) => {
          // Autoplay failed, handle the error here
          console.log("Autoplay failed:", error);
        });
      }
    };

    const handleMouseEnter = (event) => {
      if (event.target === video) {
        // Mouse entered the video
        playAudio();
      }
    };

    const handleMouseLeave = () => {
      // Mouse left the video
      audio.pause();
    };

    video.addEventListener("mouseenter", handleMouseEnter);
    video.addEventListener("mouseleave", handleMouseLeave);

    return () => {
      video.removeEventListener("mouseenter", handleMouseEnter);
      video.removeEventListener("mouseleave", handleMouseLeave);
      if (audio) {
        audio.pause();
        audio.currentTime = 0;
      }
    };
  }, []);

  return (
    <React.Fragment>
      <MainHeader headerTitle="Hashira Fanpage" bannerLoc="/images/banner.jpeg" />
      <div className={classes.container}>
        <video ref={videoRef} src="videos/TheHashiras.mp4" autoplay loop muted>
          {/* Add additional video sources if needed */}
        </video>
      </div>
      <Navigation />
      <audio ref={audioRef} src={hashiraTheme} loop />
    </React.Fragment>
  );
}

export default HomePage;
