import React, { useEffect, useRef } from "react";
import MainHeader from "./MainHeader";
import Navigation from "./Navigation";
import hashiraTheme from "./hashiraTheme.mp3"
import classes from "./HomePage.module.css"


function HomePage() {
    const audioRef = useRef(null);
    const videoRef = useRef(null);

    useEffect(() => {
      const audio = audioRef.current;
      const video =videoRef.current;
  
      const playAudio = () => {
        if (audio) {
          audio.volume = 0.5; // Set the volume to 50%
          audio.play().catch((error) => {
            // Autoplay failed, handle the error here
            console.log("Autoplay failed:", error);
          });
        }
      };
  
      playAudio(); // Call the function immediately
  
      return () => {
        if (audio) {
          audio.pause();
          audio.currentTime = 0;
        }
      };
    }, []);

  return (
    <React.Fragment >
      <MainHeader headerTitle="鬼滅の刃" bannerLoc="/images/kimetsu-no-yaiba-logo.png" />
      <video className={classes.mainVideo}src="videos/TheHashiras.mp4" autoPlay loop muted>
      </video>
      <Navigation />
      <audio ref={audioRef} src={hashiraTheme} loop />
    </React.Fragment>
  );
}

export default HomePage;
